import React, { useState, useEffect } from 'react';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';

const TaskForm = ({ initialData = {}, onSubmit, onCancel }) => {
  const [title, setTitle] = useState(initialData.title || '');
  const [description, setDescription] = useState(initialData.description || '');
  const [dueDate, setDueDate] = useState(initialData.dueDate ? new Date(initialData.dueDate) : null);
  const [dueTime, setDueTime] = useState(initialData.dueTime || '09:00');
  const [priority, setPriority] = useState(initialData.priority || 'medium');
  const [pinned, setPinned] = useState(initialData.pinned || false);
  const [error, setError] = useState('');

  const timePresets = [
    { label: 'Morning', value: '09:00' },
    { label: 'Afternoon', value: '14:00' },
    { label: 'Evening', value: '19:00' },
  ];

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!title.trim()) {
      setError('Title is required');
      return;
    }
    onSubmit({
      title: title.trim(),
      description: description.trim(),
      dueDate: dueDate ? dueDate.toISOString().split('T')[0] : null,
      dueTime,
      priority,
      pinned,
    });
  };

  return (
    <form className="task-form" onSubmit={handleSubmit}>
      <h3>{initialData.id ? 'Edit Mission' : 'Create New Mission'}</h3>
      {error && <p className="error-text">{error}</p>}

      <div className="form-group">
        <label>Mission Title *</label>
        <input
          type="text"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          placeholder="What needs to be done?"
          autoFocus
        />
      </div>

      <div className="form-group">
        <label>Description</label>
        <textarea
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          placeholder="Additional intel..."
          rows="3"
        />
      </div>

      <div className="form-row-grid">
        <div className="form-group">
          <label>Due Date</label>
          <DatePicker
            selected={dueDate}
            onChange={(date) => setDueDate(date)}
            dateFormat="yyyy-MM-dd"
            placeholderText="Select Date"
            isClearable
          />
        </div>
        <div className="form-group">
          <label>Due Time</label>
          <input
            type="time"
            value={dueTime}
            onChange={(e) => setDueTime(e.target.value)}
          />
          <div className="time-presets">
            {timePresets.map(preset => (
              <button
                key={preset.label}
                type="button"
                className={`preset-btn ${dueTime === preset.value ? 'active' : ''}`}
                onClick={() => setDueTime(preset.value)}
              >
                {preset.label}
              </button>
            ))}
          </div>
        </div>
      </div>

      <div className="form-row-grid">
        <div className="form-group">
          <label>Priority</label>
          <select value={priority} onChange={(e) => setPriority(e.target.value)} className="option-select">
            <option value="high">High (Combat)</option>
            <option value="medium">Medium (Active)</option>
            <option value="low">Low (Routine)</option>
          </select>
        </div>
        <div className="form-group" style={{ display: 'flex', alignItems: 'flex-end' }}>
          <label className="pin-checkbox">
            <input
              type="checkbox"
              checked={pinned}
              onChange={(e) => setPinned(e.target.checked)}
            />
            ­ƒôî Pin Mission
          </label>
        </div>
      </div>

      <div className="form-actions">
        <button type="submit" className="btn-action btn-submit">
          {initialData.id ? 'Authorize Update' : 'Initialize Mission'}
        </button>
        <button type="button" className="btn-action btn-cancel" onClick={onCancel}>
          Abort
        </button>
      </div>
    </form>
  );
};

export default TaskForm;

import React, { createContext, useContext, useReducer, useEffect } from 'react';
import { useLocalStorage } from '../hooks/useLocalStorage';
import { useNotifications } from '../hooks/useNotifications';

const TaskContext = createContext();

const initialState = {
  tasks: [],
  filter: 'all',        // 'all', 'active', 'completed'
  sortBy: 'date',       // 'date', 'priority'
  sortOrder: 'asc',     // 'asc', 'desc'
  toasts: [],           // [{ id, message, type }]
  searchTerm: '',
};

const MAX_STORAGE_BYTES = 4 * 1024 * 1024; // 4MB Limit

function taskReducer(state, action) {
  switch (action.type) {
    case 'ADD_TOAST':
      return { ...state, toasts: [...state.toasts, { id: Date.now(), ...action.payload }] };
    case 'REMOVE_TOAST':
      return { ...state, toasts: state.toasts.filter(t => t.id !== action.payload) };
    case 'SET_TASKS':
      return { ...state, tasks: action.payload };
    case 'ADD_TASK':
      return { ...state, tasks: [...state.tasks, action.payload] };
    case 'UPDATE_TASK':
      return {
        ...state,
        tasks: state.tasks.map((task) =>
          task.id === action.payload.id ? { ...task, ...action.payload } : task
        ),
      };
    case 'DELETE_TASK':
      return {
        ...state,
        tasks: state.tasks.filter((task) => task.id !== action.payload),
      };
    case 'TOGGLE_COMPLETE':
      return {
        ...state,
        tasks: state.tasks.map((task) =>
          task.id === action.payload
            ? { ...task, completed: !task.completed }
            : task
        ),
      };
    case 'TOGGLE_PIN':
      return {
        ...state,
        tasks: state.tasks.map((task) =>
          task.id === action.payload
            ? { ...task, pinned: !task.pinned }
            : task
        ),
      };
    case 'SET_FILTER':
      return { ...state, filter: action.payload };
    case 'SET_SORT':
      return { ...state, sortBy: action.payload.sortBy, sortOrder: action.payload.sortOrder };
    case 'SET_SEARCH':
      return { ...state, searchTerm: action.payload };
    default:
      return state;
  }
}

export const TaskProvider = ({ children }) => {
  const [state, dispatch] = useReducer(taskReducer, initialState);
  const { storedValue: storedTasks, setValue: setStoredTasks } = useLocalStorage('tasks', []);

  // Sync localStorage with state.tasks
  useEffect(() => {
    if (storedTasks && storedTasks.length > 0) {
      dispatch({ type: 'SET_TASKS', payload: storedTasks });
    }
  }, []);

  // Storage Monitor & Pruning Logic
  useEffect(() => {
    const dataString = JSON.stringify(state.tasks);
    const sizeInBytes = new Blob([dataString]).size;

    // Auto-pruning if nearing 4MB limit
    if (sizeInBytes >= MAX_STORAGE_BYTES) {
      console.warn('Storage limit approaching. Pruning oldest tasks...');
      // Keep pinned tasks and 50 most recent tasks
      const sortedByAge = [...state.tasks].sort((a, b) => a.id - b.id);
      const toKeep = sortedByAge.slice(Math.floor(sortedByAge.length * 0.2)); // Remove oldest 20%
      dispatch({ type: 'SET_TASKS', payload: toKeep });
    }

    setStoredTasks(state.tasks);
  }, [state.tasks, setStoredTasks]);

  // Notifications for due tasks
  useNotifications(state.tasks);

  return (
    <TaskContext.Provider value={{ state, dispatch }}>
      {children}
    </TaskContext.Provider>
  );
};

export const useTasks = () => useContext(TaskContext);

import React, { createContext, useContext, useEffect, useState } from 'react';

const ThemeContext = createContext();

export const ThemeProvider = ({ children }) => {
  const [darkMode, setDarkMode] = useState(() => {
    const saved = localStorage.getItem('darkMode');
    return saved ? JSON.parse(saved) : false;
  });

  useEffect(() => {
    localStorage.setItem('darkMode', JSON.stringify(darkMode));
    if (darkMode) {
      document.documentElement.classList.add('dark');
    } else {
      document.documentElement.classList.remove('dark');
    }
  }, [darkMode]);

  const toggleDarkMode = () => setDarkMode((prev) => !prev);

  return (
    <ThemeContext.Provider value={{ darkMode, toggleDarkMode }}>
      {children}
    </ThemeContext.Provider>
  );
};

export const useTheme = () => useContext(ThemeContext);

import { useState, useEffect } from 'react';

export const useLocalStorage = (key, initialValue) => {
  const [storedValue, setStoredValue] = useState(() => {
    try {
      const item = window.localStorage.getItem(key);
      return item ? JSON.parse(item) : initialValue;
    } catch (error) {
      console.error(error);
      return initialValue;
    }
  });

  const setValue = (value) => {
    try {
      const valueToStore = value instanceof Function ? value(storedValue) : value;
      setStoredValue(valueToStore);
      window.localStorage.setItem(key, JSON.stringify(valueToStore));
    } catch (error) {
      console.error(error);
    }
  };

  return { storedValue, setValue };
};
